<summary>Code from StackBlitz</summary>

```ts
import { Component, Pipe, PipeTransform } from '@angular/core'
import { CommonModule } from '@angular/common'

interface Foo {
  bar?: number
}

@Pipe({ name: 'assertNumber', standalone: true })
export class AssertNumberPipe implements PipeTransform {
  transform (value: number): number {
    return value
  }
}

@Pipe({ name: 'assertFoo', standalone: true })
export class AssertFooPipe implements PipeTransform {
  transform (value: Foo): Foo {
    return value
  }
}

@Component({
  selector: 'app-root',
  template: `
      <!-- Works as expected (just like in the TypeScript equivalent) -->
      @if (foo && foo.bar) {
          {{ foo | assertFoo }}
          {{ foo.bar | assertNumber }}
      }

      <!-- Both lines throw an error (the equivalent works in TypeScript) -->
      @if (foo?.bar) {
          {{ foo | assertFoo }}
          {{ foo.bar | assertNumber }}
      }
  `,
  standalone: true,
  imports: [
    CommonModule,
    AssertNumberPipe,
    AssertFooPipe,
  ],
})
export class AppComponent {
  foo?: Foo = { bar: 1 }

  test () {
    // Works as expected (just like the template equivalent)
    if (this.foo && this.foo.bar) {
      new AssertFooPipe().transform(this.foo)
      new AssertNumberPipe().transform(this.foo.bar)
    }

    // Works as expected (but the equivalent fails in the template)
    if (this.foo?.bar) {
      new AssertFooPipe().transform(this.foo)
      new AssertNumberPipe().transform(this.foo.bar)
    }
  }
}
```

</details>

