### Description

I'm encountering an issue where the translation for MatPaginator works the first time, but if I change the language in the pre-login phase and then enter a specific screen, the language-specific information does not display correctly. It always shows "Items per page" in the default language.

`import { Injectable, OnDestroy } from '@angular/core';
import { MatPaginatorIntl } from '@angular/material/paginator';
import { TranslateService } from '@ngx-translate/core';
import { Subject } from 'rxjs';
import { take, takeUntil } from 'rxjs/operators';

@Injectable()
export class CustomMatPaginatorIntl extends MatPaginatorIntl implements OnDestroy {
  private _destroyed$ = new Subject<void>();
  private _label_of = 'of';

  constructor(private translate: TranslateService,) {
    super();
    this.translate.onLangChange.pipe(takeUntil(this._destroyed$))
      .subscribe({
        next: () => {
          this._getAndInitTranslations();
        },
        error: err => console.error('Error subscribing to language changes', err)
      });
      setTimeout(() => {
        this.changes.next();
        this._getAndInitTranslations();
      }, 100);
  }


  ngOnDestroy(): void {
    this._destroyed$.next();
    this._destroyed$.complete();
  }

  /**
   * Retrieves and initializes translations for common items per page and table of content.
   */
  private _getAndInitTranslations(): void {
    this.translate
      .get([
        'MX_AC_COMMON.COMMON_ITEMS_PER_PAGE',
        'MX_AC_COMMON.COMMON_TABLE_OF',
      ])
      .subscribe({
        next: translation => {
          const itemPerPage = translation['MX_AC_COMMON.COMMON_ITEMS_PER_PAGE'] || 'Items per page';
          this.itemsPerPageLabel = `${itemPerPage}:`;
          this._label_of = translation['MX_AC_COMMON.COMMON_TABLE_OF'] || 'of';
          this.changes.next();
          console.log(this.itemsPerPageLabel);
        },
        error: err => console.error('Error retrieving translations', err)
      });
  }

  getRangeLabel = (page: number, pageSize: number, length: number): string => {
    if (length === 0 || pageSize === 0) {
      return `0 ${this._label_of} ${length}`;
    }
    length = Math.max(length, 0);
    const startIndex = page * pageSize;
    const endIndex = startIndex < length
      ? Math.min(startIndex + pageSize, length)
      : startIndex + pageSize;
    return `${startIndex + 1} - ${endIndex} ${this._label_of} ${length}`;
  };
}
`

