  public header = HeaderComponent;
  public sides: any;
  public tableData: any;
  public monthNames = (<any>names).names;
  public month = [];
  public cities: any;
  public all: any;
  public options = {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  };
  public begin = null;
  public end = null;
  public tableColumns: string [] = [
    'select', 'city', 'address',
    'material', 'format', 'side',
    'type', 'price_for_belarus',
    'price_for_foreign',
    'one',
    'two', 'three', 'four',
    'five', 'six', 'seven',
    'eight', 'nine', 'ten',
    'eleven', 'twelve'
  ];
  public selection = new SelectionModel<Array<any>>(true, []);

  constructor(private httpClient: AppHttpService,
              private pptxgenService: AppPptxjsService,
              private dialog: MatDialog,
              private scheduleService: AppSideScheduleService,
              private element: ElementRef,
              private snack: MatSnackBar
  ) { }

  @ViewChild(MatPaginator) paginator: MatPaginator;
  @ViewChild(MatSort) sort: MatSort;

  ngOnInit() {
    this.fillMonthes();
    this.getSides();
    this.getCities();
    this.addresses();
  }

  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.tableData.data.length;
    return numSelected === numRows;
  }

  masterToggle() {
    this.isAllSelected() ?
        this.selection.clear() :
        this.tableData.data.forEach(row => this.selection.select(row));
  }

