  makeRegExp(value) {
    return new RegExp(value, 'i');
  }

  applyFilter(filterValue: string) {
    this.tableData.filter = filterValue.trim().toLowerCase();
  }

  convertDate(string) {
    let formated = null;
    let splitted = null;
    month.forEach((item) => {
      if (string.search(item.name) > -1) {
        splitted = string.replace(this.makeRegExp(item.name), `${item.month}`).replace(/ –≥./, '').split(' ');
        formated = splitted[2] + '-' + splitted[1] + '-' + splitted[0];
      }
    });
    return new Date(formated);
  }

  getCities() {
    this.httpClient.getCities().subscribe(
        data => {
          this.cities = data;
        },
        error => {
          console.log(error);
        }
    );
  }

