import {Component, signal} from '@angular/core';
import {httpResource} from '@angular/common/http';
import {FormsModule} from '@angular/forms';

@Component({
    selector: 'index-item',
    imports: [
        FormsModule
    ],
    template: `
        <input type="number" [(ngModel)]="userId">
        @if (userResource.isLoading()) {
            <span>loading...</span>
        } @else {
            <span>Complete！</span>
            @if (userResource.value().code) {
                <span>data Length:{{ userResource.value().data.length }}</span>
            }
        }
    `
})
export class Item {
    userId = signal(1);
    userResource = httpResource<any>(() =>
            `https://example.com/v1/users/${this.userId()}`,
        {
            defaultValue: {
                code: 200,
                data: [1, 2, 3, 4, 5, 6, 7, 8, 9]
            },
        }
    )
}
```

