  selector: 'app-root',
  template: `
    <div #element></div>
  `
})
export class App {
  protected query1 = viewChild('element');
  protected query2 = viewChild('element') as Signal<unknown>;

  constructor() {
    afterNextRender(() => {
      console.log('query1', this.query1()); // ✅ Works as espected
      console.log('query2', this.query2()); // ❌ Returns undefined
    })
  }
}
```

