        new TerserWebpackPlugin({
          terserOptions: {
            compress: {
              drop_console: true
            }
          }
        })
      ]
    };

    if (!webpackConfig.plugins) {
      webpackConfig.plugins = [];
    }

    if (env.conf) {
      // Replace config with config.prod file
      webpackConfig.plugins.push(
        new webpack.NormalModuleReplacementPlugin(/(.*)configuration\/config(\.*)/, function (resource) {
          resource.request = resource.request.replace(/configuration\/config/, `configuration/config.${env.conf}`);
        })
      );
    }
  }else {
    webpackConfig.mode = 'development';
    webpackConfig.devtool = 'inline-source-map';
    webpackConfig.output = {
      path: path.resolve(__dirname),
      filename: `electron-main.dev.js`
    };
  }

  return webpackConfig;
};

