})
export class ExampleComponent
  implements AfterContentInit, AfterViewInit
{
  @ViewChild(MatMenu, { static: true }) readonly menu?: MatMenu;

  private readonly trigger: MatMenuTrigger;

  constructor(
    overlay: Overlay,
    element: ElementRef<HTMLElement>,
