$myTheme: mat.define-light-theme(
  (
    color: (
      primary: $theme-primary,
      accent: $theme-accent,
      warn: $theme-warn,
    ),
    is-dark: false,
    foreground: $mat-light-theme-foreground,
    background: $mat-light-theme-background,
  )
);

$myAltTheme: mat.define-dark-theme(
  (
    color: (
      primary: $theme-primary,
      accent: $theme-accent,
      warn: $theme-warn,
    ),
    is-dark: true,
    foreground: $mat-dark-theme-foreground,
    background: $mat-dark-theme-background,
  )
);

$theme: $myTheme;
$altTheme: $myAltTheme;


//for body and normal html components
@mixin body-theme($theme) {
    $_background: map-get($theme, background);
    $app-background-color: mat-color($_background, background);
  
    body {
      background-color: $app-background-color;
    }
}

@mixin dashboardcontent-theme($theme) {
    $_background: map-get($theme, background);
    $_foreground: map-get($theme, foreground);
    $primary: map-get($theme, primary);
    $accent: map-get($theme, accent);
    $warn: map-get($theme, warn);
    $background-color: mat-color($_background, background);
    $foreground-color: mat-color($_foreground, text);
    $primary-color: mat-color($primary);
    $accent-color: mat-color($accent);
    $warn-color: mat-color($warn);
   
  
    .dashboardcontent {
      color: $foreground-color;
    }
    .content-inner{
        background:$background-color;
      
    }
    footer  p{
        
        color: $accent-color;
    }
}


// Theme Init
@include mat.all-legacy-component-themes($theme);
@include body-theme($theme);
@include dashboardcontent-theme($theme);

.theme-alternate {
  @include mat.all-legacy-component-themes($altTheme);
  @include body-theme($altTheme);
  @include dashboardcontent-theme($altTheme);
}

