export class NgUtils {
  private static readonly _ngMonkeyPatchKey = '__ngContext__';
  private static readonly _ngHost = 0;
  private static readonly _ngTViewIdx = 1;
  private static readonly _ngContextIdx = 8;

  public static getComponentFromElement<TComponent>(elem: Element): TComponent {
    if (elem == null) {
      return null;
    }

    // get angular context from element
    const mpValue = elem[this._ngMonkeyPatchKey];

    if (Array.isArray(mpValue) && typeof mpValue[this._ngTViewIdx] === 'object') {
      const tView = mpValue[this._ngTViewIdx];
      if (tView == null) {
        return null;
      }

      const cmpIndices = tView.components;
      if (cmpIndices != null && cmpIndices.length > 0) {
        for (let idx = 0; idx < cmpIndices.length; ++idx) {
          const cmpIndex = cmpIndices[idx];
          const mpValueCmp = mpValue[cmpIndex];

          if (!Array.isArray(mpValueCmp) || typeof mpValueCmp[this._ngContextIdx] !== 'object') {
            continue;
          }

          if (mpValueCmp[this._ngHost] === elem) {
            return mpValueCmp[this._ngContextIdx];
          }
        }
      }
    }

    return null;
  }
}
