app-table.html
```html
<mat-card appearance="outlined">
    <mat-card-content>
        @if (toolbarRequired) {
            <app-toolbar (onToolbarButtonClick)="onToolbarButtonClick($event)"
                         [customButtons]="toolbarCustomButtons"
                         [requiredBaseButtons]="requiredBaseButtons"
                         [disabled]="isLoading"
            />
        }

        <div class="app-table">
            <table
                mat-table
                [dataSource]="dataSource"
                matSort
                (matSortChange)="onSortChange($event)"
                [matSortActive]="useDefaultSort ? defaultColumnForSort : ''"
                [matSortDirection]="useDefaultSort ? defaultSortDirection : ''"
                class="mat-elevation-z8"
                cdkDropList cdkDropListLockAxis="x"
                cdkDropListOrientation="horizontal"
                (cdkDropListDropped)="dropColumn($event)"
            >
                <ng-container matColumnDef="{{selectColumnDefaultName}}">
                    <th mat-header-cell *matHeaderCellDef cdkDrag>
                        <mat-checkbox (change)="$event ? toggleAllRows() : null"
                                      [disabled]="isLoading"
                                      [checked]="selection.hasValue() && isAllSelected()"
                                      [indeterminate]="selection.hasValue() && !isAllSelected()">
                        </mat-checkbox>
                    </th>
                    <td mat-cell *matCellDef="let row">
                        <mat-checkbox (click)="$event.stopPropagation()"
                                      (change)="$event ? selection.toggle(row) : null"
                                      [checked]="selection.isSelected(row)">
                        </mat-checkbox>
                    </td>
                </ng-container>

                @for (column of columns; track column.name) {
                    <ng-container
                        *ngIf="column.name !== this.selectColumnDefaultName"
                        matColumnDef="{{column.name}}"
                    >
                        <th
                            mat-header-cell
                            *matHeaderCellDef
                            [mat-sort-header]="column.name"
                            [disabled]="column.sortable === undefined ? !defaultColumnsValues.sortable : !column.sortable"
                            sortActionDescription="Sort by {{column.name}}"
                            class="app-table-header-row"
                            cdkDrag
                        >
                            {{ column.displayName }}
                        </th>

                        <td mat-cell *matCellDef="let element">
                            @if (column.valueRenderer) {
                                {{ column.valueRenderer(getRecordValue(element, column.dataIndex)) }}
                            } @else {
                                {{ getRecordValue(element, column.dataIndex) }}
                            }
                        </td>
                    </ng-container>
                }

                <tr mat-header-row
                    *matHeaderRowDef="getColumnsNames()"
                    style="border: 1px solid var(--app-theme-table-border-color)"
                ></tr>

                <tr mat-row
                    [className]="selection.isSelected(row) ? 'table-data-row-selected' : 'table-data-row'"
                    *matRowDef="let row; columns: getColumnsNames();"
                    style="border: 1px solid var(--app-theme-table-border-color)"
                    (click)="setRowSelected(row)"
                ></tr>
            </table>

            <div *ngIf="isLoading"
                 style="display: flex; justify-content: center; align-items: center; background: white; margin-top: 20px;">
                <mat-progress-spinner
                    diameter="30"
                    color="primary"
                    mode="indeterminate">
                </mat-progress-spinner>
            </div>
        </div>

        <div class="app-table-footer">
            <app-button class="refresh-button"
                        icon="refresh"
                        (click)="onRefreshClick()"
                        [matTooltip]="EmsowAngular.Translator.translate('refresh')"
                        [disabled]="isLoading"
            />
            <mat-paginator [pageSize]="useRemotePagination ? paginatorPageSize : paginatorDefaultPageSize"
                           [pageIndex]="useRemotePagination ? paginatorPageIndex : null"
                           [length]="useRemotePagination ? paginatorLength : null"
                           [pageSizeOptions]="paginatorPagesSizes"
                           [showFirstLastButtons]="showFirstLastButton"
                           [disabled]="isLoading"
                           (page)="useRemotePagination ? onPageChange() : null"
                           aria-label="Select page of periodic elements"
            >
            </mat-paginator>
        </div>
    </mat-card-content>
</mat-card>
