    protected getFormValues(): IKeyable[]
    {
        let result: IKeyable[] = [];

        if (!this.fields) {
            return result;
        }

        let allFields: AppCollapsibleFieldsetElement[] = [],
            fields = this.fields.toArray();

        if (EmsowAngular.isEmpty(fields)) {
            return result;
        }

        fields.map(function (field) {
            field.elements.map(function(element) {
                allFields.push(element);
            });
        }, this);

        EmsowAngular.each(allFields, function (field: AppCollapsibleFieldsetElement) {
            result.push({
                ...field,
                value: field.getValue(field.element)
            });
        });

        return result;
    }

