export class AutoCompleteComponent extends FormAccessor implements OnInit {
	@ViewChild('primaryInput') public primaryInput: ElementRef;
	public readonly options = input.required<AutoCompleteOption[]>();
	public readonly placeholder = input<string>();
	public readonly showClearButton = input<boolean>(false);
	public readonly searchOptions: OutputEmitterRef<string> = output<string>();
	public readonly valueSelected: OutputEmitterRef<MatAutocompleteSelectedEvent> =
		output<MatAutocompleteSelectedEvent>();

	public selectedValue: MatAutocompleteSelectedEvent;
	public readonly i18nKeys = I18nKeys;

	public initForm(): FormControl {
		return new FormControl<string>('');
	}

	public ngOnInit(): void {
		this.initialized$
			.pipe(
				// Denis: only proceed when the form is initialized.
				filter(Boolean),
				// Denis: switch to the valueChanges of the form.
				switchMap(() => this.form.valueChanges),
				// Denis: do not proceed when the value hasn't changed.
				distinctUntilChanged(),
				// Denis: provide a sensible debounceTime to wait for user input.
				debounceTime(500),
				// Denis: if the value is empty, set the selectedValue to null te reset.
				tap((value: string) => {
					if (typeof value !== 'string' || value === '') {
						this.selectedValue = null;
					}
				}),
				// Denis: filter out string values (only string input)
				filter((value: string) => typeof value === 'string'),
				// Denis: filter out the selected value (to avoid additional searches for a selected value)
				filter((value: string) => value !== this.selectedValue?.option?.value),
				// Denis: emit to the parent to trigger a search for new options
				tap((value: string) => this.searchOptions.emit(value)),
				// Denis: clean up on destroy
				takeUntil(this.destroy$)
			)
			.subscribe();

		super.ngOnInit();
	}

	public handleSelectedValue(value: MatAutocompleteSelectedEvent): void {
		this.selectedValue = value;

		this.valueSelected.emit(value);
	}

	public handleClearValue(): void {
		this.form.setValue('');
	}
}
