Here's one of my tests
```
/* eslint-disable max-classes-per-file */

import { TestBed, waitForAsync, ComponentFixture } from '@angular/core/testing'
import { ActivatedRoute } from '@angular/router'
import { By } from '@angular/platform-browser'
import { BrowserAnimationsModule } from '@angular/platform-browser/animations'
import { TranslateModule } from '@ngx-translate/core'
import { Store } from '@ngxs/store'
import { Observable, of } from 'rxjs'

import { SharedModule } from '../../shared/modules/shared.module'
import { AuthService } from '../../core/services'
import { LoginComponent } from './login.component'
import { PhoneNumberModule } from '../../shared/modules/phone-number/phone-number.module'
import { Organization } from '../../core/interfaces/organization/organization'

describe('LoginComponent', () => {
  let fixture: ComponentFixture<LoginComponent>
  let fakeAuthService: AuthService

  class ActivatedRouteMock {
    queryParams = new Observable((observer) => {
      const urlParams = { redirectTo: '' }
      observer.next(urlParams)
      observer.complete()
    })
  }

  class StoreMock {}

  beforeEach(waitForAsync(() => {
    fakeAuthService = jasmine.createSpyObj<AuthService>('AuthService', {
      loginAdminDashboard: of({ clubs: [{ name: 'Organization' } as Organization], sponsors: [] })
    })

    TestBed.configureTestingModule({
      declarations: [LoginComponent],
      imports: [TranslateModule.forRoot(), BrowserAnimationsModule, SharedModule, PhoneNumberModule],
      providers: [
        { provide: ActivatedRoute, useClass: ActivatedRouteMock },
        { provide: Store, useClass: StoreMock },
        { provide: AuthService, useValue: fakeAuthService }
      ]
    }).compileComponents()

    fixture = TestBed.createComponent(LoginComponent)
    fixture.detectChanges()
  }))

  it('submits the form succesfully', () => {
    fixture.componentInstance.loginForm.patchValue({
      phoneCountryCode: '+47',
      phoneMobile: '91910101',
      password: 'password'
    })

    const form = fixture.debugElement.query(By.css(`[data-testid="form"]`))
    form.triggerEventHandler('ngSubmit')

    expect(fakeAuthService.loginAdminDashboard).toHaveBeenCalled()
  })

  it(`does not submit the form when it's not valid`, () => {
    fixture.componentInstance.loginForm.patchValue({
      phoneCountryCode: '+47',
      phoneMobile: '',
      password: ''
    })

    const form = fixture.debugElement.query(By.css(`[data-testid="form"]`))
    form.triggerEventHandler('ngSubmit')

    expect(fakeAuthService.loginAdminDashboard).not.toHaveBeenCalled()
  })
})
