### Description

Created a button that the user hits to center the map on their current location.

The component code is as follows:

`import { Component, OnInit } from '@angular/core';
import {ApiService} from "../../../services/api.service";
import {Job} from "../../../models/Job";
import {BehaviorSubject, ReplaySubject} from "rxjs";
import {ActivatedRoute} from "@angular/router";
import {JobAddress} from "../../../models/JobAddress";

@Component({
  selector: 'app-single-job',
  templateUrl: './single-job.component.html',
  styleUrls: ['./single-job.component.scss']
})
export class SingleJobComponent implements OnInit {


  job: BehaviorSubject<Job | null>;
  loading$: BehaviorSubject<boolean>;
  activeTab: BehaviorSubject<number>;
  id: number;

  center: google.maps.LatLngLiteral = {
    lat: 52.403761,
    lng: -1.174439
  };

  mapOptions: google.maps.MapOptions = {
    zoomControl: false,
    zoom: 8,
    streetViewControl: false,
  }

  constructor(public api: ApiService, public route: ActivatedRoute) {
    this.job = new BehaviorSubject<Job | null>(null);
    this.activeTab = new BehaviorSubject<number>(1);
    this.loading$ = new BehaviorSubject<boolean>(true);
  }

  ngOnInit(): void {
    this.route.params.subscribe((data)=>{
      if(data.id){
        this.id = data.id;
        this.refreshData();
      }
    });
  }

  refreshData(){

    this.api.apiGet('/tenant-api/jobs/' + this.id).then((GetJobResult)=>{
      this.job.next(GetJobResult);
      this.loading$.next(false);
    }).catch((Error)=>{
      console.log(Error);
    })

  }

  findMyLocation(){


    navigator.geolocation.getCurrentPosition((data) => {
      this.center = {
        lat: data.coords.latitude,
        lng: data.coords.longitude
      };
      this.mapOptions.zoom = 12;
    });


  }




}
`


