2. public onDrag(event: CdkDragDrop<string[]>): void {
    if (event.previousContainer === event.container) {
      const PREV_IND_WITH_OFFSET: number = this.range ? this.range.start + event.previousIndex  : event.previousIndex;
      const CUR_IND_WITH_OFFSET: number = this.range ? this.range.start + event.currentIndex : event.currentIndex ;
      moveItemInArray(  this.selectedDimension.memberSelections, PREV_IND_WITH_OFFSET, CUR_IND_WITH_OFFSET);
      this.memberSelections = [...this.memberSelections];
    } else {
      transferArrayItem(event.previousContainer.data,
        event.container.data,
        event.previousIndex,
        event.currentIndex);
    }
