import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { PageTemplateComponent } from '../../templates/page-template/page-template.component';
import { MatFormFieldModule } from "@angular/material/form-field";
import { MatIconModule } from "@angular/material/icon";
import { MatInputModule } from "@angular/material/input";
import { MatDialogModule } from "@angular/material/dialog";
import { MatButtonModule } from "@angular/material/button";
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { Subject, Observable, shareReplay, tap, filter, switchMap, map, catchError } from 'rxjs';
import { LoadingService } from 'src/app/components/loading/loading.service';
import { NotificationService } from 'src/app/components/notification/notification.service';
import { Router, RouterModule } from '@angular/router';
import { AuthService } from '../auth.service';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, PageTemplateComponent, ReactiveFormsModule, MatFormFieldModule, MatIconModule, MatInputModule, MatDialogModule, MatButtonModule, RouterModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent {
  form: FormGroup = this._fb.nonNullable.group({
    Identifier: this._fb.nonNullable.control('', {
      validators: [Validators.required],
    }),
    Password: this._fb.nonNullable.control('', {
      validators: [Validators.required],
    }),
  });

  submit_state$: Subject<boolean> = new Subject<boolean>();
  formMessage$: Observable<void> = this.submit_state$
    .pipe(takeUntilDestroyed())
    .pipe(shareReplay({ bufferSize: 1, refCount: true }))
    .pipe(
      tap((x) => {
        if (x === false) {
          this._loadingService.close();
        } else {
          this._loadingService.show();
        }
      }),
      filter((x) => x === true),
      switchMap(() => this._authService.login(this.form.value)),
      tap(() => this.submit_state$.next(false)),
      tap((lastPath) => {
        this._router.navigateByUrl(
          lastPath !== null ? lastPath : '',
          { replaceUrl: true }
        );
      }),
      switchMap(() =>
        this._notifService
          .show({
            title: 'Sukses',
            message: 'Berhasil masuk',
            type: 'success',
          })
          .afterClosed()
      ),
      map((x) => void 0),
      takeUntilDestroyed(),
      catchError((error, source) => {
        this.submit_state$.next(false);
        return source; // to make observable alive again. because observable die if fail
      })
    );
  constructor(
    private _authService: AuthService,
    private _fb: FormBuilder,
    private _router: Router,
    private _loadingService: LoadingService,
    private _notifService: NotificationService
  ) { }

  submit(): void {
    if (this.form.valid) {
      this.submit_state$.next(true);
    }
  }
}

