	sendMessage = worker.port.postMessage.bind(worker.port);
	worker.port.start();
} else {
	console.warn('App is running per in per tab basis');
		worker = new Worker(workerUrl, { name: 'fetcher' });
	worker.onmessage = (event: MessageEvent) => this.messages$.next(event.data);
	sendMessage = worker.postMessage.bind(worker);
}
