## üî¨ Minimal Reproduction
just create classes as below:

```
export class FGroup extends FormGroup {
  _setControl?: (name: string | number, control: AbstractFControl) => void;

  addControl(name: string, control: AbstractFControl): void {
    this._setControl(name, control);
    super.addControl(name, control);
  }

  setControl(name: string, control: AbstractFControl): void {
    this._setControl(name, control);
    super.setControl(name, control);
  }
}

export abstract class AbstractFControl extends AbstractControl {
  _dto?: AbstractDTO;
  dto?: AbstractDTO;
  cfg?: AbstractControl;
  changed ? = false; // —Ñ–ª–∞–≥ —á—Ç–æ –∏–∑–º–µ–Ω–µ–Ω –∏ –±–µ—Ä–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏
  childKey?: string | number;
  parent: FGroup | FArray;
  path?: string;
  fullPath?: string;

  _updateValue?() {}

  get(path: (string | number)[] | string) {
    return super.get(path) as AbstractFControl;
  }

  initAllValidators?(includeChildren = false, updStatus = false) {
    if (!this.cfg) return;
    this.setValidators(this.cfg.validator);
    this.setAsyncValidators(this.cfg.asyncValidator);
    if (updStatus) this.updateValueAndValidity();
    if (includeChildren) this.getChildren()
      .forEach(ch => ch.initAllValidators(true, updStatus));
  }

  clearAllValidators?(includeChildren = false, updStatus = false) {
    this.clearValidators();
    this.clearAsyncValidators();
    if (updStatus) this.updateValueAndValidity();
    if (includeChildren) this.getChildren()
      .forEach(ch => ch.clearAllValidators(true, updStatus));
  }

  updateState?() {
    super.updateValueAndValidity();
    this.getChildren().forEach(ch => ch.updateState());
  }

  init?(data, conf: AbstractControl = null) {
    const confValidator = CObject.get(conf, 'validator');
    const confAsyncValidator = CObject.get(conf, 'asyncValidator');
    if (this.validator !== confValidator) this.setValidators(confValidator);
    if (this.asyncValidator !== confAsyncValidator) this.setAsyncValidators(confAsyncValidator);
    this.cfg = conf;
    if (data !== undefined) this._dto = data;
    this.updateValueAndValidity();

    if (CObject.get(conf, 'controls')) this.updChildrenCfg(conf);
  }

  setParentAndKey?(parent: FGroup | FArray, childKey: string | number) {
    if (!TypeHelper.isNullOrUndefined(childKey)) this.childKey = childKey;
    if (parent) this.setParent(parent);
  }

  getChildren?() {
    return Object.values(CObject.get(this, 'controls') || []) as AbstractFControl[];
  }

  childHasError?(errCode: string) {
    return !!this.getChildren()
      .find(ch => {
        return ch.hasError(errCode) ||
          ch instanceof ControlContainer && ch.childHasError(errCode);
      });
  }

  updChildrenCfg?(conf: AbstractControl) {
    if (conf instanceof FormGroup) {
      Object.keys(conf.controls).forEach(key => {
        const childCtrl = this.get(key) as AbstractFControl;
        if (childCtrl) childCtrl.init(undefined, conf.controls[key]);
      });
    } else if (conf instanceof FormArray) {
      const dataArr = this.getDto() || [];
      if (this instanceof FGroup) {
        const controlsLength = Object.keys(this.controls || {}).length - 1;
        for (let i = controlsLength; i > dataArr.length - 1; i--) {
          this.removeControl(i.toString());
        }
      }
      dataArr.forEach((d, index) => {
        (this.get(index.toString()) as AbstractFControl)
          .init(undefined, conf.controls[0]);
      });
    }
  }

  getPath?() {
    return TypeHelper.isNullOrUndefined(this.childKey) ? '' : this.childKey.toString();
  }

  getFullPath?() {
    const fullPath = CObject.get(this.parent, 'fullPath');
    return (fullPath ? `${fullPath}.` : '') + this.getPath();
  }

  getDto?(path?: string) {
    const dto = this._dto !== undefined ? this._dto : this.parentDto();
    return path ? CObject.get(dto, path) : dto;
  }

  setDto?(dto: AbstractDTO) { this.parentDto(dto); }

  parentDto?(value?: AbstractDTO) {
    let parent = this.parent;
    let parentDto;
    while (parent) {
      parentDto = parent._dto;
      if (parentDto) break;
      parent = parent.parent;
    }
    const dtoPath = this.fullPath.replace(parent ? new RegExp(`^${parent.fullPath}\\.`) : '', '');
    if (value === undefined) return CObject.get(parentDto, dtoPath);
    CObject.set(parentDto, dtoPath, value);
    this.updateValueAndValidity({});
  }
}

export class CObject {
  static get(
    item: object,
    key: string | (string | number)[],
    defaultVal = null
  ) {
    if (!item || !key) return defaultVal;
    const keyArr = typeof key === 'string' ? key.split('.') : key;
    const itemKey = keyArr.shift();
    let result = item[ itemKey ];

    if (result === undefined) {
      if (item instanceof Array && isNaN(+itemKey)) {
        const resultArr = Array.from(item.keys())
          .map(_key => {
            keyArr.unshift(_key, itemKey);
            return this.get(item, keyArr);
          })
          .filter(res => res !== null);
        return resultArr.length ? resultArr : defaultVal;
      }
      result = defaultVal;
    }
    return result && keyArr.length ? this.get(result, keyArr, defaultVal) : result;
  }

  static set(
    item: object,
    key: string,
    val
  ) {
    const keyParts = key.split('.');
    const itemName = keyParts.pop();
    item = keyParts.length ? this.get(item, keyParts) : item;
    if (item) item[ itemName ] = val;
  }
}
