```typescript
@Directive()
export abstract class InputAbstractComponent<T> implements ControlValueAccessor {
  public readonly componentId = this.constructor.name.toLowerCase();
  public static nextId = 0;
  public abstract id: string;
  public onChange: (_: T) => void = () => {};
  public onTouched: () => void = () => {};

  @Input({ transform: booleanAttribute }) public required!: boolean;
  @Input({ transform: booleanAttribute }) public disabled!: boolean;

  public abstract writeValue(value: T): void;

  public registerOnChange(fn: (_: T) => void): void {
    this.onChange = fn;
  }
  public registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }
  public setDisabledState?(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }
  public validate(_: FormControl): ValidationErrors | null {
    return _.valid ? null : { [this.componentId]: { valid: false, message: this.componentId } };
