    this._setState(0, options);
  }
} catch (e) {
  onUnexpectedError(e);
}
```

This is the minified code:
```ts
try {
    try {
        for (var t = fs(this._asyncIterable), i, n, s; i = !(n = yield t.next()).done; i = !1) {
            let r = n.value;
            r && (this._result.push(r),
            this._fireResult(e))
        }
