                Éµhmr0.ÉµÉµelementEnd();
            }
            if (rf & 2) {
                Éµhmr0.ÉµÉµadvance();
                Éµhmr0.ÉµÉµtextInterpolate(ctx.title);
            }
        },
        styles: ["\n\n.title[_ngcontent-%COMP%] {\n  font-size: x-large;\n}\n/*# sourceMappingURL=component.css.map */"]
    });
    (() => {
        (typeof ngDevMode === "undefined" || ngDevMode) && Éµhmr0.ÉµsetClassDebugInfo(AppComponent, {
            className: "AppComponent",
            filePath: "src/component.ts",
            lineNumber: 8
        });
    })();
}
```

