                    <span> {{item.value}}
                    </span>

                </th>

            </tr>
        </thead>
        <tbody>

            <tr *ngFor="let itemDad of data|keyvalue; let i=index;**trackBy:sameDaddy**">
                {{sameDaddy(i,itemDad)}}
                <td class="align-middle">
                    <input [attr.disabled]='disableCheckBox(itemDad)' (click)="toogleCheck(itemDad.value)" type="checkbox" />
                </td>
                <td class="align-middle" *ngFor="let itemChild of itemDad.value|keyvalue;let j=index;trackBy:sameChild;">
                    {{itemChild.value?itemChild.value:"Sin valor"}}
                </td>
                <td class="align-middle">
                    <button (click)="handlerAccion(data[i])" class="btn "><span style='color: rgb(148, 138, 138)'><i
                                class='fas fa-map-marker-alt'></i></span></button>
                    <!-- <button class='btn btn-ls buttonUniversal '  name='Mapa' ><div class='data' hidden data='(${JSON.stringify({ pos: index, data: data })})'></div><span style='color: rgb(148, 138, 138)'><i class='fas fa-map-marker-alt'></i></span></button> -->
