**My personal work-around currently employed**
<!-- ‚úçÔ∏èIs this a browser specific issue? If so, please specify the browser and version. -->
```javascript
// SomeClass (non-TS)
Object.defineProperty(SomeClass, "normalizeUnnecessaryUrlEscapesCausedByAngular", {
	value: function(popStateUri) {
		var href = popStateUri ? popStateUri : SomeClass.currentHref();
		if (SomeClass.PLUS_HEX_REGEX.test(href)) {
			var href2 = href.replace(SomeClass.PLUS_HEX_REGEX, SomeClass.PLUS);
			if (SomeClass.logger.isDebugEnabled())
				SomeClass.logger.debug("normalizeUnnecessaryUrlEscapesCausedByAngular: " +href+ " vs. " +href2+ " vs. " +popStateUri);
			history.replaceState(history.state, document.title, href2 /* "/some/thing+" */);
		}
	},
	writable: false
});

// AppComponent
import { AppIntegrationFacade } from '../../../../common/classes/AppIntegrationFacade';
export class AppComponent implements OnInit {
  ngOnInit() {
    this.subscribeToEvents();
  }
  private subscribeToEvents() {
    this.router.events.subscribe(val => {
      if (val instanceof NavigationEnd) {
        AppIntegrationFacade.normalizeUnnecessaryUrlEscapesCausedByAngular(val.urlAfterRedirects);
      }
    });
  }
}

// AppIntegrationFacade
import { environment /*...*/ } from '../../projects/something/src/environments/environment';
export class AppIntegrationFacade {
  public static normalizeUnnecessaryUrlEscapesCausedByAngular(url: string) {
    SomeClass.normalizeUnnecessaryUrlEscapesCausedByAngular(url);
  }
}
