@Component({
  selector: 'validation-error',
  standalone: true,
  providers: [
    ValidationService,
    LocalizationService
  ],
  templateUrl: './validation-error.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ValidationErrorComponent {

  protected readonly validationService: ValidationService;

  public field: InputSignal<AbstractControl | null | undefined> = input.required({ alias: 'field' });
  public constraints: InputSignal<AnyObject> = input({}, { alias: 'constraints' });

  public label: InputSignal<string> = input('This field', { alias: 'label' });
  public singleError: InputSignal<boolean> = input(false, { alias: 'single-error' });

  /**
   * Creates an instance of the class and initializes the validation service.
   *
   * @param {ValidationService} validationService - The service responsible for validation logic.
   */
  public constructor(validationService: ValidationService) {
    this.validationService = validationService;
  }

  /**
   * Retrieves all validation error messages for the current control.
   *
   * @returns {string[]} An array of validation error messages.
   * If there are no errors, the array will be empty.
   */
  private getErrorMessages(): string[] {
    const errors: string[] = [];
    // Check if the control exists before attempting to gather error messages
    if (this.controlExists) {
      // Iterate over the control's error codes and collect their corresponding messages
      this.controlErrorCodes.forEach((code: string): void => {
        if (this.hasError(code)) {
          // Add message for each error code
          errors.push(this.getMessage(code).concat(' '));
        }
      });
    }

    // Return the collected error messages
    return this.finalizeErrors(errors);
  }

  /**
   * Finalizes the list of errors based on the singleError setting.
   *
   * If `singleError` is true and there is at least one error, only the first error
   * is returned; otherwise, the full list of errors is returned.
   *
   * @param errors - The list of error messages.
   * @returns A single-element array with the first error if `singleError` is true;
   *          otherwise, the original list of errors.
   */
  public finalizeErrors(errors: string[] = []): string[] {
    // Return only the first error if singleError mode is enabled
    if (this.isSingleError && errors.length > 0) {
      return [errors[0]];
    }
    // Return all errors if singleError mode is disabled
    return errors;
  }

  /**
   * Retrieves a validation message for a specific error code.
   *
   * @param {string} errorCode - The code representing the validation error.
   * @returns {string} The validation message associated with the error code.
   */
  protected getMessage(errorCode: string): string {
    if (this.isMessageAvailable && this.handlerExists(errorCode)) {
      // Retrieve the validation message handler for an error code
      const handler: MessageHandler = this.getHandler(errorCode);
      // Get a localized message based on the error code
      return handler(this.control, this.controlLabel, this.controlOptions);
    }
    return errorCode;
  }

  /**
   * Checks if a validation function exists for a given error code.
   *
   * @param errorCode - The error code to check.
   * @returns True if a validation function exists; otherwise, false.
   */
  protected handlerExists(errorCode: string): boolean {
    return truthy(this.validationService.messageHandler[errorCode]);
  }

  /**
   * Retrieves the validation message associated with a given error code.
   *
   * @param errorCode - The error code for which to get the validation message.
   * @returns The validation function associated with the specified error code.
   */
  protected getHandler(errorCode: string): MessageHandler {
    return this.validationService.messageHandler[errorCode];
  }

  /**
   * Checks if the control contains a specific validation error code.
   *
   * @param {string} errorCode - The error code to check for in the control's error list.
   * @returns {boolean} `true` if the control has the specified error code, `false` otherwise.
   */
  protected hasError(errorCode: string): boolean {
    return this.controlErrors ? this.hasErrors(errorCode) : false;
  }

  /**
   * Checks if a specific error code exists in control errors.
   *
   * @param errorCode The error code to check.
   * @returns `true` if the error code exists, otherwise `false`.
   */
  protected hasErrors(errorCode: string): boolean {
    // Check if the controlErrors object contains the specified errorCode
    return this.controlErrors.hasOwnProperty(errorCode);
  }

  /**
   * Determines whether the control is invalid and has been interacted with (either touched or dirty).
   *
   * @returns {boolean} `true` if the control exists, is invalid, and has been touched or is dirty; otherwise, `false`.
   */
  protected get isInvalidAndTouched(): boolean {
    return this.controlExists && this.state.invalid && (this.state.dirty || this.state.touched);
  }

  /**
   * Retrieves validation error messages if the control is invalid and touched.
   *
   * @returns {string[]} An array of validation error messages if the control is invalid and touched, otherwise an empty array.
   */
  protected get errors(): string[] {
    return this.isInvalidAndTouched ? this.getErrorMessages() : [];
  }

  /**
   * Checks if the control is not null or undefined.
   *
   * @returns {boolean} `true` if the control exists (is truthy), `false` otherwise.
   */
  protected get controlExists(): boolean {
    return truthy(this.control);
  }

  /**
   * Retrieves the list of error codes for the control's validation errors.
   *
   * @returns {string[]} An array of error codes (as strings) representing the validation errors
   * associated with the control. If the control is not present, returns an empty array.
   */
  protected get controlErrorCodes(): string[] {
    return truthy(this.control) && truthy(this.controlErrors)
      ? Object.keys(this.controlErrors)
      : [];
  }

  /**
   * Retrieves the list of validation errors from the control.
   *
   * @returns {ValidationErrors | any | null | undefined} An object containing
   * the validation errors for the control, `null`, `undefined`, or another type
   * if no control or errors are present.
   */
  protected get controlErrors(): ValidationErrors | any | null | undefined {
    return this.control.errors;
  }

  /**
   * Checks if a validation message is available.
   *
   * This getter calls the isMessageAvailable property of the validation service.
   *
   * @returns True if a message is available, otherwise false.
   */
  protected get isMessageAvailable(): boolean {
    // Retrieves the message availability status from the validation service
    return this.validationService.isMessageAvailable;
  }

  /**
   * Retrieves the current form control, ensuring it's not null or undefined.
   * This method relies on the `field` function to return a valid form control.
   */
  private get control(): AbstractControl {
    return this.field()!;
  }

  /**
   * Retrieves the current state of the form control.
   * This is essentially a shortcut to reference the same control.
   */
  private get state(): AbstractControl {
    return this.control;
  }

  /**
   * Retrieves the label associated with the form control.
   * This is useful for dynamic forms that need to display control labels.
   */
  private get controlLabel(): string {
    return this.label();
  }

  /**
   * Retrieves the validation constraints or options for the form control.
   * These constraints could include things like required status or maximum length.
   */
  private get controlOptions(): AnyObject {
    return this.constraints();
  }

  /**
   * Checks if there is only a single error associated with the form control.
   * This can be useful to simplify error handling or messaging in the UI.
   */
  private get isSingleError(): boolean {
    return this.singleError();
  }

}

