        AppButtonComponent, MatTooltip, CdkDropList, CdkDrag
    ],
    templateUrl: './app-table.component.html',
    styleUrl: './app-table.component.css'
})

export default class AppTableComponent implements AfterViewInit
{
    @Input() public columns: AppTableColumn[] = [];

    @Input() public data: any = [];
    @Input() public dataRoot: string = 'data';

    private defaultUrlParameters: AppTableUrl = {
        url: '',
        timeout: Timeout.timeout
    }

    @Input() public loadUrl: AppTableUrl = this.defaultUrlParameters;
    @Input() public removeUrl: AppTableUrl = this.defaultUrlParameters;

    @Input() public toolbarRequired: boolean = true;
    @Input() public toolbarCustomButtons: AppToolbarButton[] = [];
    @Input() public requiredBaseButtons: 'all' | string[] = 'all';

    @Input() public selectRequired: boolean = true;
    @Input() public selectColumnWidth: number | undefined;

    @Input() public useRemoteSorting: boolean = true;
    @Input() public useDefaultSort: boolean = false;
    @Input() public defaultColumnForSort: string = '';
    @Input() public defaultSortDirection: SortDirection = 'asc';

    @Input() public useRemotePagination: boolean = true;

    @Input() public showFirstLastButton: boolean = true;

    protected readonly EmsowAngular = EmsowAngular;

    protected readonly queryController = new AbortController();

    protected readonly paginatorDefaultPageSize: number = 50;
    protected readonly paginatorPagesSizes: number[] = [5, 10, 25, 50, 100, 150, 200, 300, 500, 1000];

    protected readonly selectColumnDefaultName: string = 'select';

    protected defaultColumnsValues = { sortable: true };

    protected initialSelection: [] = [];
    protected allowMultiSelect: boolean = true;

    protected selection: SelectionModel<any> = new SelectionModel<any>(this.allowMultiSelect, this.initialSelection);
    protected readonly selectColumnDefaultConfig: AppTableColumn = {
        name: this.selectColumnDefaultName,
        dataIndex: '',
        width: 50,
        sortable: false,
        displayName: ''
    };

    protected isLoading: boolean = false;

    protected dataSource = new MatTableDataSource(this.data);

    @ViewChild(MatSort, { static: false }) sort!: MatSort;
    @ViewChild(MatPaginator, { static: false }) paginator!: MatPaginator;

    protected paginatorLength: number = 0;
    protected paginatorPageIndex: number = 0;
    protected paginatorPageSize: number = this.paginatorDefaultPageSize;

    // TODO: inject it in EmsowAngular. And make call like EmsowAngular.showMsg()
    private readonly dialog: MatDialog = inject(MatDialog);

    constructor(private _liveAnnouncer: LiveAnnouncer)
    {
    }

    ngOnInit(): void
    {
        if (!EmsowAngular.isEmpty(this.loadUrl)) {
            this.isLoading = true;
        }

        if (this.selectRequired) {
            let me = this,
                selectAlreadyPresent: boolean = false;

            EmsowAngular.each(this.columns, function (column: AppTableColumn) {
                if (column.name.toLowerCase() === me.selectColumnDefaultName.toLowerCase()) {
                    selectAlreadyPresent = true;

                    column.width = me.selectColumnWidth ?? column.width;

                    return;
                }
            });

            if (!selectAlreadyPresent) {
                this.selectColumnDefaultConfig.width = me.selectColumnWidth ?? this.selectColumnDefaultConfig.width;
                this.columns.splice(0, 0, this.selectColumnDefaultConfig);
            }
        }
    }

    ngAfterViewInit(): void
    {
        let me = this;

        if (!me.useRemoteSorting) {
            me.dataSource.sort = me.sort;
        }

        if (!me.useRemotePagination) {
            me.dataSource.paginator = me.paginator;
        }

        if (EmsowAngular.isEmpty(me.loadUrl.url)) {
            me.isLoading = false;

            if (EmsowAngular.isEmpty(me.data)) {
                me.data = [];
            }

            me.dataSource.data = me.data;

            return;
        }

        me.refreshGrid();

        this.applyColumnsWidth();
    }

    private applyColumnsWidth(): void
    {
        // Timeout is a crutch to display a correct width of columns
        setTimeout(() => {
            // TODO: how to avoid the code below?
            EmsowAngular.each(this.columns, function (column: AppTableColumn) {
                if (EmsowAngular.isEmpty(column.width)) {
                    return;
                }

                let columnName = column.name.replaceAll('.', '-'),
                    className = EmsowAngular.format('mat-column-{0}', [columnName]),
                    columnEl = document.getElementsByClassName(className)[0];

                if (EmsowAngular.isEmpty(columnEl) || columnEl === null) {
                    return;
                }

                columnEl?.setAttribute('style', EmsowAngular.format('width: {0}px', [column.width]))
            });
        }, 50);
    }

    public ngOnDestroy(): void
    {
        this.queryController.abort();
    }

    private cleanTableData(): void
    {
        this.data = [];
        this.dataSource.data = this.data;
    }

    public refreshGrid(filters: IKeyable[] = []): void
    {
        let me = this;
        me.isLoading = true;

        me.cleanTableData();

        let params = {};
        if (me.useRemotePagination) {
            params = EmsowAngular.mergeObjects(params, {
                limit: me.paginator.pageSize,
                start: me.paginator.pageIndex
            });
        }

        if (me.useRemoteSorting) {
            params = EmsowAngular.mergeObjects(params, {
                sort: me.sort.active,
                dir: me.sort.direction
            });
        }

        if (filters) {
            console.log(filters);
        }

        (new Fetcher).request({
            url: me.loadUrl.url,
            timeout: me.loadUrl.timeout,
            params: params,
            signal: this.queryController.signal,
            success: function (_response: any, _request: any, result: any): void {
                me.isLoading = false;

                me.data = result[me.dataRoot];
                me.dataSource.data = me.data;

                me.paginatorLength = Number(result.total);
            },
            failure: function (response: any, _request: any, result: any): void {
                me.isLoading = false;

                if (response === ResponseCodes.CANCELED) {
                    return;
                }

                me.dialog.open(AppDialogComponent, {
                    data: {
                        message: result.msg,
                        type: DialogType.ERROR
                    },
                    disableClose: true
                });
            }
        });
    }

    onSortChange(sortState: Sort): void
    {
        this.announceSortChange(sortState);

        if (this.useRemoteSorting) {
            this.refreshGrid();
        }
    }

    announceSortChange(sortState: Sort): void
    {
        sortState.direction
            ? this._liveAnnouncer.announce(EmsowAngular.format('Sorted {0}ending', [sortState.direction]))
            : this._liveAnnouncer.announce('Sorting cleared');
    }

    toggleAllRows(): void
    {
        this.isAllSelected() ?
            this.selection.clear() :
            this.dataSource.data.forEach(row => this.selection.select(row));
    }

    isAllSelected(): boolean
    {
        const numSelected: number = this.selection.selected.length,
            numRows: number = this.dataSource.data.length;

        return numSelected == numRows;
    }

    getColumnsNames(): string[]
    {
        return this.columns.map(column => column.name);
    }

    protected getRecordValue(record: any, key: string): string
    {
        let value: any = record[key];

        if (value !== undefined) {
            return value;
        }

        value = this.lookupByPath(record, key);

        return EmsowAngular.isEmpty(value) ? '-' : value;
    }

    protected lookupByPath(obj: any, path: string): string
    {
        let parts: string[] = path.split('.');
        if (parts.length == 1) {
            return obj[parts[0]];
        }
        return this.lookupByPath(obj[parts[0]], parts.slice(1).join('.'));
    }

    protected setRowSelected(row: Object): void
    {
        this.selection.setSelection(row);
    }

    protected onToolbarButtonClick(button: any)
    {
        switch (button.buttonType) {
            case 'add':
                this.onToolbarAddClick(button.button);
                break;
            case 'edit':
                this.onToolbarEditClick(button.button);
                break;
            case 'remove':
                this.onToolbarRemoveClick(button.button);
                break;
            default:
                this.onToolbarCustomButtonClick(button.buttonType, button.button);
                break;
        }
    }

    @Input() public onToolbarAddClick(button: any): void
    {
        // implement in child
    }

    @Input() public onToolbarEditClick(button: any): void
    {
        // implement in child
    }

    @Input() public onToolbarRemoveClick(button: any): void
    {
        // TODO: implement

        console.log('Remove');
    }

    @Input() public onToolbarCustomButtonClick(buttonType: any, button: any): void
    {
        // implement switch in child
    }

    @Input() public onRefreshClick(): void
    {
        this.refreshGrid();
    }

    @Input() public onPageChange(): void
    {
        this.refreshGrid();
    }

    dropColumn(event: CdkDragDrop<string[]>): void
    {
        moveItemInArray(this.columns, event.previousIndex, event.currentIndex);
        this.applyColumnsWidth();
    }
}
