@Injectable()
export class ValidationService {

  private messagesAvailable: boolean = false;
  protected readonly localization: LocalizationService;

  /**
   * Initializes an instance of the class with the provided localization service.
   *
   * @param {LocalizationService} localization - The service responsible for retrieving and managing localization messages.
   */
  public constructor(localization: LocalizationService) {
    this.localization = localization;
    this.updateMessageAvailability();
  }

  /**
   * Subscribes to the localization message availability observable
   * and updates the component's message availability status.
   */
  private updateMessageAvailability(): void {
    // Subscribe to the message availability observable
    this.localization.isMessageAvailable$.subscribe({
      next: (isAvailable: boolean): void => {
        // Update the availability status if messages are available
        if (isAvailable) {
          this.messagesAvailable = true;
        }
      }
    });
  }

  public get isMessageAvailable(): boolean {
    return this.messagesAvailable;
  }

  /**
   * Retrieves a localized message by key and replaces placeholders with provided parameters.
   *
   * The method fetches a message from the localization messages using the given key.
   * If the message contains placeholders in the format `{paramName}`, they are replaced
   * with the corresponding values from the `params` object.
   *
   * @param {string} key - The key to identify the message in the localization messages.
   * @param {object} params - An object containing key-value pairs for replacing placeholders in the message.
   * @returns {string} The localized message with placeholders replaced, or the original message if no match is found.
   */
  private getMessage(key: string, params: { [key: string]: any }): string {
    let message: string = this.localization.messages[key];
    if (falsy(message)) {
      return key;
    }

    Object.keys(params).forEach((param: string): void => {
      message = message.replace(`{${param}}`, params[param]);
    });
    return message;
  }

  public messageHandler: ValidatorObject = {
    required: (control: AbstractControl, label: string, other?: AnyObject): string => {
      return this.getMessage('required', { label, ...other });
    },
    minlength: (control: AbstractControl, label: string, other?: AnyObject): string => {
      return this.getMessage('minlength', {
        label,
        value: control.value,
        minlength: control.errors?.['minlength'].requiredLength,
        ...other,
      });
    },
    maxlength: (control: AbstractControl, label: string, other?: AnyObject): string => {
      return this.getMessage('maxlength', {
        label,
        value: control.value,
        maxlength: control.errors?.['maxlength'].requiredLength,
        ...other,
      });
    },
    min: (control: AbstractControl, label: string, other?: AnyObject): string => {
      return this.getMessage('min', {
        label,
        value: control.value,
        min: control.errors?.['min'].min,
        ...other,
      });
    },
    max: (control: AbstractControl, label: string, other?: AnyObject): string => {
      return this.getMessage('max', {
        label,
        value: control.value,
        max: control.errors?.['max'].max,
        ...other,
      });
    },
    email: (control: AbstractControl, label: string, other?: AnyObject): string => {
      return this.getMessage('email', { label, value: control.value, ...other });
    },
    boolean: (control: AbstractControl, label: string, other?: AnyObject): string => {
      return this.getMessage('boolean', { label, value: control.value, ...other });
    },
  }

}
