@Injectable()
export class LocalizationService {

  public messages: any;
  private readonly config: LocalizationConfig;
  private readonly http: HttpClient;
  private readonly locale: string;
  private readonly logger: LoggerService;

  private messageAvailableSubject: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);
  public isMessageAvailable$: Observable<boolean> = this.messageAvailableSubject.asObservable();

  /**
   * Constructs an instance of the class with the given dependencies.
   *
   * @param config - The localization configuration service.
   * @param http - The HTTP client for making requests.
   * @param logger - The logger service for logging messages.
   * @param locale - The locale identifier for localization.
   */
  public constructor(
      config: LocalizationConfigService,
      http: HttpClient,
      logger: LoggerService,
      @Inject(LOCALE_ID) locale: string) {
    this.config = config;
    this.http = http;
    this.locale = locale;
    this.logger = logger;
    this.loadMessages().then();
  }

  /**
   * Loads validation messages from the available file paths.
   *
   * This method attempts to load validation messages from a series of file paths in the order of
   * locale-specific, language-specific, and default paths. It tries each file path sequentially
   * and logs the success or failure of each attempt. If a file is successfully loaded, the messages
   * are set, and the method exits early. If all attempts fail, an error message is logged.
   *
   * @returns A promise that resolves once the messages have been loaded from one of the available file paths,
   * or logs an error if all attempts fail.
   *
   * @example
   * await loadMessages();
   * // Attempts to load files for locale and language:
   * // 'Attempting to load file: assets/i18n/ValidationMessages_en-US.json'
   * // 'Failed to load file: assets/i18n/ValidationMessages_en-US.json'
   * // 'Attempting to load file: assets/i18n/ValidationMessages_en.json'
   * // 'Loaded messages: { ... }'
   */
  private async loadMessages(): Promise<void> {
    const fileLoaders: string[] = this.getFileLoaders();

    for (const filePath of fileLoaders) {
      try {
        this.logAttempt(filePath);
        // Try to load the file at the current file path
        this.messages = await this.loadFile(filePath);
        // Log the message file if available or found
        this.logNotificationIfMessagesFound();
        // Convert messages content to a format consumed and usable by application service
        this.convertMessagesContentToJson();
        // Notify that the messages is available
        this.notifyMessageAvailable();
        // Exit the method if loading is successful
        return;
      } catch (error) {
        // Log a warning if the file fails to load and proceed to the next path
        this.logger.warn(`Failed to load file: ${filePath}`);
      }
    }

    this.logger.error('All validation message files failed to load.');
  }

  /**
   * Notifies subscribers that the validation messages is now available.
   */
  private notifyMessageAvailable(): void {
    // Emit a value to indicate that messages are available
    this.messageAvailableSubject.next(true);
  }

  /**
   * Converts the loaded messages content from a string format to a JSON object.
   *
   * This method assumes that `this.messages` contains a JSON-formatted string and
   * parses it into a JavaScript object, which is then assigned back to `this.messages`.
   *
   * @throws {SyntaxError} If the string cannot be parsed as valid JSON.
   */
  private convertMessagesContentToJson(): void {
    this.messages = JSON.parse(this.messages);
  }

  /**
   * Logs an attempt to load a specified file.
   *
   * @param filePath - The path of the file being attempted to load.
   */
  private logAttempt(filePath: string): void {
    // Log the warning with the file path
    this.logger.log(`Attempting to load file: ${filePath}`);
  }

  /**
   * Logs a notification if the provided messages object is not null.
   *
   * This method checks whether the `messages` object is non-null and, if so,
   * logs a message indicating that the file was found.
   *
   */
  private logNotificationIfMessagesFound(): void {
    // Check if the messages object is not null
    if (nonNull(this.messages)) {
      // Log a notification that the file was found
      this.logger.log('File found');
    }
  }

  /**
   * Generates a list of file paths for loading validation messages based on the current locale.
   *
   * This method retrieves the current locale and language code, then constructs a list of file paths
   * for validation messages using the configuration settings. It replaces placeholders in the paths
   * with the corresponding locale and language values.
   *
   * @returns An array of file paths in the order of locale-specific, language-specific, and default paths.
   * If any path is not configured, it returns an empty string for that path.
   *
   * @example
   * // Assuming the locale is 'en-US' and language code is 'en', the returned paths might be:
   * [
   *   'assets/messages/en-US.json',
   *   'assets/messages/en.json',
   *   'assets/messages/default.json'
   * ]
   */
  private getFileLoaders(): string[] {
    const languageCode: string = this.getLanguageCode(this.locale);
    const config: ValidationMessagesConfig = this.config.getConfig();

    return [
      config.localeSpecificPath?.replace('${locale}', this.locale) || '',
      config.languageSpecificPath?.replace('${language}', languageCode) || '',
      config.defaultPath || '',
    ];
  }

  /**
   * Loads a file from the specified file path and returns its content as a string.
   *
   * This method performs an HTTP GET request to fetch the file content.
   * If the request is successful, the file content is resolved as a string.
   * If the request fails, the method rejects with an error containing the file path.
   *
   * @param filePath - The path of the file to be loaded.
   * @returns A Promise that resolves to the file content as a string, or rejects with an error if the file cannot be loaded.
   *
   * @example
   * loadFile('assets/config.json')
   *   .then((content: string) => {
   *     console.log('File content:', content);
   *   })
   *   .catch((err: Error) => {
   *     console.error(err.message);
   *   });
   */
  private loadFile(filePath: string): Promise<string> {
    return new Promise((resolve, reject): void => {
      this.http.get(filePath, { responseType: 'text' }).subscribe({
        next: (messages: string) => resolve(messages),
        error: () => reject(new Error(`Failed to load file: ${filePath}`)),
      });
    });
  }

  /**
   * Extracts the language code from a given locale string.
   *
   * The method splits the locale string by the hyphen ('-') separator,
   * which is commonly used in locale formats (e.g., "en-US", "fr-FR"),
   * and returns the first part of the string, representing the language code.
   *
   * @param locale - The locale string from which the language code will be extracted (e.g., "en-US", "fr-FR").
   * @returns The language code as a string (e.g., "en" for "en-US").
   *
   * @example
   * getLanguageCode('en-US'); // Returns 'en'
   * getLanguageCode('fr-FR'); // Returns 'fr'
   */
  private getLanguageCode(locale: string): string {
    let separator: string = '-';
    if (locale.includes('-')) {
      separator = '-';
    }
    return locale.split(separator)[0];
  }

}
