### Alternatives considered

```ts
import { EnvironmentInjector, inject, runInInjectionContext, Injector } from '@angular/core';

export function InjectableEnvironment() {
  return function <T extends { new (...args: any[]): {} }>(target: T) {
    const Original = target;

    return class extends Original {
      injector = inject(EnvironmentInjector);
      constructor(...args: any[]) {
        super(...args);
      }
    };
  };
}

export function InContext() {
  return function (
    target: any,
    propertyKey: string,
    descriptor: PropertyDescriptor
  ) {
    const original = descriptor.value;

    descriptor.value = function (...args: any[]) {
      const injector: EnvironmentInjector | undefined = this.injector;
      if (!injector) {
        throw new Error(
          `@InContext(): this.injector not found. Add @InjectableEnvironment().`
        );
      }

      return runInInjectionContext(injector, () => original.apply(this, args));
    };

    return descriptor;
  };
}

@InjectableEnvironment()
@Injectable({ providedIn: 'root' })
export class SomeService {
  @InContext()
  doStuff() {
    const s = inject(MyService);
    s.doSomething();
  }
}
