import {APP_BASE_HREF} from '@angular/common';
import express, {Express, Request, Response} from 'express';
import {CommonEngine} from '@angular/ssr';
import {
    InjectionToken,
} from '@angular/core';
import bootstrapServerApp from '@src/bootstrap/web/main.server';
import {dirname, resolve, join} from 'node:path';
import {fileURLToPath} from 'node:url';

export const REQUEST = new InjectionToken<Request>('REQUEST');
export const RESPONSE = new InjectionToken<Response>('RESPONSE');

export function app(): express.Express {
    const server: Express = express();
    // server.use(cors());
    /*server.use(
      compression({
        level: 9,
        threshold: 0,
      }),
    );*/

    const serverDistFolder: string = dirname(fileURLToPath(import.meta.url));
    const browserDistFolder: string = resolve(serverDistFolder, '../browser');
    const indexHtml: string = join(serverDistFolder, 'index.server.html');

    const commonEngine = new CommonEngine({
        enablePerformanceProfiler: true,
    });

    server.set('view engine', 'html');
    server.set('views', browserDistFolder);

    // Example Express Rest API endpoints
    // server.get('/api/**', (req, res) => { });
    // Serve static files from /browser
    server.get('*.*', express.static(browserDistFolder, {
        maxAge: '1y'
    }));

    // All regular routes use the Angular engine
    server.get('*', (req, res, next) => {
        const {protocol, originalUrl, baseUrl, headers} = req;
        console.log('Inside server.ts server.get');

        commonEngine
            .render({
                bootstrap: bootstrapServerApp,
                documentFilePath: indexHtml,
                url: `${protocol}://${headers.host}${originalUrl}`,
                publicPath: browserDistFolder,
                providers: [
                    {provide: APP_BASE_HREF, useValue: baseUrl},
                    {provide: RESPONSE, useValue: res},
                    {provide: REQUEST, useValue: req}
                ],
            })
