export const IS_LOGGING_MESSAGES$ = new InjectionToken<BehaviorSubject<boolean>>('isLoggingMessages$', {
    providedIn: 'root',
    factory: function () {
        const typeThis = this;
        type typeThisWithObsCache = { isLoggingMessages$: BehaviorSubject<boolean> } & typeof typeThis;
        const self = this as typeThisWithObsCache;
        
        if(!self.isLoggingMessages$)
        {
            const IS_LOGGING_MESSAGES_KEY = 'IS_LOGGING_MESSAGES_KEY';
            self.isLoggingMessages$ = new BehaviorSubject<boolean>(localStorage.getItem(IS_LOGGING_MESSAGES_KEY) === 'false' ? false : true);
