    PropertyInfo property = typeof(Worker).GetProperty(_left);
    MemberExpression memberAccess = Expression.MakeMemberAccess(entityParam, property);
    ConstantExpression right = Expression.Constant(_right);

     var op = ExpressionType.Default;
     switch (_operator)
     {
         case ("=="):
             op = ExpressionType.Equal;
             break;
     }

    BinaryExpression binary = Expression.MakeBinary(op, memberAccess, right);

    Expression<Func<Worker, bool>> expression = Expression.Lambda(binary, entityParam) as 
    Expression<Func<Worker, bool>>;

    // This generates a null reference error, even though I know the expression is valid.
    Func<Worker, bool> predicate = expression.Compile();
