### Additional context

```
export default function createBlazorForm(name: string, fields: {}): FormData {
  const result = new FormData();

  // Set Blazor form handler
  result.append("_handler", name);

  // Set antiforgery token
  const el = document.querySelector("input[name=__RequestVerificationToken]");
  if (!el) {
    throw new Error("Unable to find antiforgery token on page");
  }

  result.append("__RequestVerificationToken", el.value);

  // Set fields
  Object.keys(fields).forEach(x => {
    result.append(x, fields[x]);
  });

  return result;
}
