	/// </summary>
	[MethodImpl(MethodImplOptions.AggressiveInlining)]
	protected void StartRenderTrace()
	{
		JsInProcessRuntime.InvokeVoid("console.time",
			GetComponentId(startedRenderId++));
	}

	/// <summary>
	/// Should be called in <see cref="OnAfterRenderAsync"/> or <seealso cref="ComponentBase.OnAfterRender"/>. By default is called in <see cref="OnAfterRenderAsync"/>.
	/// </summary>
	[MethodImpl(MethodImplOptions.AggressiveInlining)]
	protected void EndRenderTrace()
	{
		JsInProcessRuntime.InvokeVoid("console.timeEnd",
			GetComponentId(finishedRenderId++));
	}

	protected override async Task OnAfterRenderAsync(bool firstRender)
	{
		await base.OnAfterRenderAsync(firstRender);
		EndRenderTrace();
	}

	private int startedRenderId = 0;
	private int finishedRenderId = 0;
	private string? componentName;

	private string GetComponentFullName()
	{
