	"type": "object",
	"anyOf": [
		{
			"$ref": "#/components/schemas/PetCat"
		},
		{
			"$ref": "#/components/schemas/PetDog"
		},
		{
			"$ref": "#/components/schemas/PetBase"
		}
	]
},
"PetBase": {
	"properties": {
		"name": {
			"type": "string"
		}
	}
},
"PetDog": {
	"required": [
		"$dis"
	],
	"properties": {
		"$dis": {
			"enum": [
				"dog"
			],
			"type": "string"
		},
		"breed": {
			"type": "string",
			"nullable": true
		},
		"name": {
			"type": "string"
		}
