**Controller**

    [Route("api/[controller]")]
    public class LogInOutController : Controller
    {  
        private readonly SignInManager<ApplicationUser> _SignInManager;
        private readonly ApplicationUserService _ApplicationUserService;
        public readonly WebSiteSettings _Settings;
        public readonly IServiceProvider _ServiceProvider;
        private readonly IHttpContextAccessor _HttpContextAccessor;
        public LogInOutController(IServiceProvider serviceProvider, SignInManager<ApplicationUser> signInManager, ApplicationUserService applicationUserService, IOptions<WebSiteSettings> Settings, IHttpContextAccessor httpContextAccessor)
        {
            
            _SignInManager = signInManager;
            _ApplicationUserService = applicationUserService;
            _Settings = Settings.Value;
            _ServiceProvider = serviceProvider;
            _HttpContextAccessor = httpContextAccessor;
        }
 
        [HttpPost]
        [Route("LoginUser")]
        public async Task<JsonResult> LoginUser([FromBody] LoginModel model)
        {
            if (ModelState.IsValid && model.ManageMessageId == ManageMessageId.LoginIdentity.ToString() && !string.IsNullOrEmpty(model.Username))
            {
                var result = await _SignInManager.PasswordSignInAsync(model.Username, model.Password, model.RememberMe, lockoutOnFailure: false);
                if (result.Succeeded)
                {
                    var timeStamp = Guid.NewGuid().ToString();
                    if (await _ApplicationUserService.UpdateApplicationUserLoginSecurityTimeStamp(model.Username, timeStamp))
                    {
                        return Json(new LoginResult() { ID = model.Username, AuthenticationCookie = _HttpContextAccessor.HttpContext.Response.Headers["Set-Cookie"].ToString(), TimeStamp = timeStamp, ManageMessageId = ManageMessageId.LoginSuccess.ToString() });
                    }
                }
            }
            await _HttpContextAccessor.HttpContext.SignOutAsync(IdentityConstants.ExternalScheme);
            await _HttpContextAccessor.HttpContext.SignOutAsync();
            await _SignInManager.SignOutAsync();
            return Json(new LoginResult());
        }   
        
        [HttpPost]      
        [Route("GetCurrentUserPost")]
        public async Task<JsonResult> GetCurrentUserPost([FromBody] CurrentUserResult result)
        {
            if (result != null && result.ManageMessageId == ManageMessageId.GetCurrentIdentity.ToString())
            {
                if (_HttpContextAccessor.HttpContext.User != null && !string.IsNullOrEmpty(_HttpContextAccessor.HttpContext.User.Identity.Name) && _HttpContextAccessor.HttpContext.User.Identity.IsAuthenticated)
                {               
                    ApplicationUser userFinded = null;
                    using (var scope = _ServiceProvider.CreateScope())
                    {
                        var context = scope.ServiceProvider.GetService<ApplicationDbContext>();
                        userFinded = await context.Users.Where(p => p.UserName == _HttpContextAccessor.HttpContext.User.Identity.Name)
                                                        .Select(p=> new ApplicationUser 
                                                        { 
                                                            UserName=p.UserName, 
                                                            LoginSecurityTimeStamp=p.LoginSecurityTimeStamp 
                                                        }
                                                        ).AsNoTracking().FirstOrDefaultAsync();
                    }
                       
                    if (userFinded != null && (result.IsInitialStateFromBrowser || (result.TimeStamp == userFinded.LoginSecurityTimeStamp && result.ID == userFinded.UserName)))
                    {
                        result.ID = userFinded.UserName;
                        result.TimeStamp = userFinded.LoginSecurityTimeStamp;
                        return Json(result);
                    }
                }
            }
            await _HttpContextAccessor.HttpContext.SignOutAsync(IdentityConstants.ExternalScheme);
            await _HttpContextAccessor.HttpContext.SignOutAsync();
            await _SignInManager.SignOutAsync();
            return Json(new CurrentUserResult());
        }
}

