2. Create CustomId.cs file in Shared project with following content:
```csharp
using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace BlazorApp1.Shared;

[TypeConverter(typeof(CustomIdConverter))]
public struct CustomId
{
    public static CustomId Empty { get; }

    public int First { get; set; }
    public int Second { get; set; }

    private const string ID_SEPARATOR = ":";

    public override string ToString()
    {
        return $"{First}{ID_SEPARATOR}{Second}";
    }

    public static CustomId Parse(string text)
    {
        if (string.IsNullOrEmpty(text))
        {
            return CustomId.Empty;
        }

        string[] parts = text.Split(ID_SEPARATOR);
        int left = int.Parse(parts[0], CultureInfo.CurrentCulture);
        int right = int.Parse(parts[1], CultureInfo.CurrentCulture);
        return new CustomId() { First = left, Second = right };
    }
}

public class CustomIdConverter : TypeConverter
{
    public override bool CanConvertFrom(ITypeDescriptorContext context, Type sourceType)
    {
        if (sourceType == typeof(string))
        {
            return true;
        }

        return base.CanConvertFrom(context, sourceType);
    }

    public override object ConvertFrom(
        ITypeDescriptorContext context,
        CultureInfo culture,
        object value
    )
    {
        if (value is string text)
        {
            return CustomId.Parse(text);
        }

        return base.ConvertFrom(context, culture, value);
    }
}
