{
    options.AddPolicy(authPolicyConfig.PolicyName, policy => {
        policy.AddAuthenticationSchemes(new strin[]{"scheme1","scheme2"});
        policy.RequireAuthenticatedUser();
        policy.Requirements.Add(new AuthPolicyRequirementsReq1(authPolicyConfig));
