CustomRemoteAuthenticationService class
```csharp
public class CustomRemoteAuthenticationService : RemoteAuthenticationService<RemoteAuthenticationState, RemoteUserAccount, ApiAuthorizationProviderOptions>
{
    public CustomRemoteAuthenticationService(IJSRuntime jsRuntime,
        IOptionsSnapshot<RemoteAuthenticationOptions<ApiAuthorizationProviderOptions>> options,
        NavigationManager navigation,
        AccountClaimsPrincipalFactory<RemoteUserAccount> userFactory) : base(jsRuntime, options, navigation, userFactory)
    {
    }

    protected override async ValueTask<ClaimsPrincipal> GetAuthenticatedUser()
    {
        var account = await GetAccount();
        return await AccountClaimsPrincipalFactory.CreateUserAsync(account, Options.UserOptions);
    }

    private async ValueTask<RemoteUserAccount> GetAccount()
    {
        if (!await IsCookieExists())
        {
            await RemoveLocalAccount();
            return null;
        }

        var account = await GetLocalAccount();

        if (account == null || account.AdditionalProperties.Count == 0)
        {
            await EnsureAuthService();
            account = await JsRuntime.InvokeAsync<RemoteUserAccount>("AuthenticationService.getUser");
            if (account != null && account.AdditionalProperties.Count > 0)
            {
                await SetLocalAccount(account);
            }
        }

        return account;
    }

    private async ValueTask EnsureAuthService()
    {
        await (ValueTask)GetType().BaseType
            .GetMethod(nameof(EnsureAuthService), BindingFlags.NonPublic | BindingFlags.Instance)
            .Invoke(this, null);
    }

    private async ValueTask<bool> IsCookieExists()
    {
        return await JsRuntime.InvokeAsync<bool>("containsCookie", "idsrv.session");
    }

    private async ValueTask<RemoteUserAccount> GetLocalAccount()
    {
        var accountStr = await JsRuntime.InvokeAsync<string>("localStorage.getItem", "userAccount");
        if (string.IsNullOrWhiteSpace(accountStr))
        {
            return null;
        }

        return JsonSerializer.Deserialize<RemoteUserAccount>(accountStr);
    }

    private async ValueTask SetLocalAccount(RemoteUserAccount account)
    {
        if (account == null)
        {
            await RemoveLocalAccount();
            return;
        }

        var accountStr = JsonSerializer.Serialize(account);
        await JsRuntime.InvokeVoidAsync("localStorage.setItem", "userAccount", accountStr);
    }

    private async ValueTask RemoveLocalAccount()
    {
        await JsRuntime.InvokeVoidAsync("localStorage.removeItem", "userAccount");
    }
}
```

