</code></pre>
<pre><code>public static class CascadingStateServiceCollectionExtensions
{
    public static IServiceCollection AddNotifyingCascadingValue&lt;TState, TImplementation&gt;(
        this IServiceCollection services, bool isFixed = <span class="hljs-literal">false</span>)
        <span class="hljs-built_in">where</span> TState : class, INotifyPropertyChanged
        <span class="hljs-built_in">where</span> TImplementation : class, TState
    {
        // Register the implementation as Scoped
        services. AddScoped&lt;TImplementation&gt;();

<span class="hljs-built_in">return</span> services. AddCascadingValue&lt;TState&gt;(sp =&gt;
        {
            var state = sp. GetRequiredService&lt;TImplementation&gt;();
            <span class="hljs-built_in">return</span> new CascadingStateValueSource&lt;TState&gt;(state, isFixed);
        });
    }

// Overload <span class="hljs-keyword">for</span> cases <span class="hljs-built_in">where</span> TState and TImplementation are the same
    public static IServiceCollection AddNotifyingCascadingValue&lt;TState&gt;(
        this IServiceCollection services, bool isFixed = <span class="hljs-literal">false</span>)
        <span class="hljs-built_in">where</span> TState : class, INotifyPropertyChanged
    {
        <span class="hljs-built_in">return</span> AddNotifyingCascadingValue&lt;TState, TState&gt;(services, isFixed);
    }

private sealed class CascadingStateValueSource&lt;T&gt;
        : CascadingValueSource&lt;T&gt;, IDisposable <span class="hljs-built_in">where</span> T : INotifyPropertyChanged
