        {
            var stopWatch = new Stopwatch();
            stopWatch.Start();

            for (int num = 0; num <= n; num++)
            {
                ctr = 0;

                for (i = 2; i <= num / 2; i++)
                {
                    if (num % i == 0)
                    {
