  option (google.api.http) = {
    get: "/api/notes/list",
  };
}

message ListRequest {
  ListRequestRecursive tree = 1;
}
message ListRequestRecursive {
  ListRequestRecursiveChildren children = 1;
}
message ListRequestRecursiveChildren {
  repeated ListRequestRecursive  children = 1;
}
