/// <returns>The task object representing the asynchronous operation.</returns>
public virtual Task<IdentityResult> ValidateAsync(UserManager<TUser> manager, TUser user, string? password)
{
    /* Some code omitted */

    int[] charTypes = CalculatePassword(password);

    /* Some code omitted */

    if (options.RequireNonAlphanumeric && charTypes[(int)CharTypes.Other] == 0)
    {
        errors.Add(Describer.PasswordRequiresNonAlphanumeric());
    }
    if (options.RequireDigit && charTypes[(int)CharTypes.Digit] == 0)
    {
        errors.Add(Describer.PasswordRequiresDigit());
    }
    if (options.RequireLowercase && charTypes[(int)CharTypes.Lower] == 0)
    {
        errors.Add(Describer.PasswordRequiresLower());
    }
    if (options.RequireUppercase && charTypes[(int)CharTypes.Upper] == 0)
    {
        errors.Add(Describer.PasswordRequiresUpper());
    }
    if (charTypes.Sum() < options.RequiredCharTypes)
    {
        errors.Add(new() { Code = "TooFewCharType", Description = string.Format("Passwords must use at least {0} different type of characters.", CharTypeMinCount) });
    }
    return
        Task.FromResult(errors.Count == 0
            ? IdentityResult.Success
            : IdentityResult.Failed(errors.ToArray()));
}
