public class LdapSignInManager : SignInManager<UserIdentity> 
{
    #region Private fields

    private readonly ILogger<LdapSignInManager> _logger;

    #endregion

    #region Constructors

    /// <summary>
    /// Builds an Ldap SignInManager instance.
    /// </summary>
    /// <param name="userManager">The ldap user manager instance object.</param>
    /// <param name="contextAccessor">The context accessor object.</param>
