    public LdapSignInManager(LdapUserManager userManager,
        IHttpContextAccessor contextAccessor,
        IUserClaimsPrincipalFactory<UserIdentity> claimsFactory,
        IOptions<IdentityOptions> optionsAccessor,
        ILogger<LdapSignInManager> logger,
        IAuthenticationSchemeProvider schemes)
        : base(userManager, contextAccessor, claimsFactory, optionsAccessor, logger, schemes)
    {
        _logger = logger;
    }

    #endregion

    #region Public methods 

    /// <summary>
    /// Validates the user's password.
    /// </summary>
    /// <param name="user">The user's identity object.</param>
    /// <param name="password">The user's password.</param>
    /// <param name="rememberMe">Indicates whether to remember the user credentials or not.</param>
    /// <returns>A signin result object.</returns>
    public async Task<SignInResult> PasswordSignInAsync(UserIdentity user, string password, bool rememberMe)
    {
        try
        {
            // Validates UserManager object
            LdapUserManager? ldapUserManager = null;
            if (UserManager is LdapUserManager)
            {
                ldapUserManager = UserManager as LdapUserManager;
                if (ldapUserManager is null)
                {
                    _logger.LogError("LdapSignInManager - An error occured while signing the user to active directory.");
                    return SignInResult.Failed;
                }
            }
            else
            {
                _logger.LogError("LdapSignInManager - An error occured while signing the user to active directory.");
                return SignInResult.Failed;
            }

            // Validates Ldap account signin password
            ldapUserManager.AuthenticationType = AuthenticationType.Ldap;
            var ldapUser = await ldapUserManager.FindAsync(user, password);
            if (ldapUser is null)
            {
                _logger.LogError("LdapSignInManager - An error occured while signing the user to active directory.");
                return SignInResult.Failed;
            }

            // Validates user password
            return await base.PasswordSignInAsync(ldapUser, password, rememberMe, user.LockoutEnabled);
        }
        catch (Exception ex)
        {
            _logger.LogError("LdapSignInManager - An error occured while signing the user to active directory.\n\n{ex.Message}\n\n{ex.StackTrace}\n\n", ex.Message, ex.StackTrace);
            return SignInResult.Failed;
        }
    }

    #endregion
}


