                try
                {
                    var response = await httpClient.GetAsync($"/ProdMinutes/ProdMinutes?department={department}&startDate={startDate}&enddate={endDate}");
                    if(response.IsSuccessStatusCode)
                    {
                        var prodMin = await response.Content.ReadFromJsonAsync<ProdMinutesSeries>();
