        {
            long nextNanoSec = e.Timestamp;
            long dtNano = nextNanoSec - nanoSecond;
            double dtSeconds = dtNano * 1e-9;

if (0 < dtNano) // Does not accept repeating timestamps or out-of-order readings.
            {
                double w, x, y, z;
                x = e.Values[0];
                y = e.Values[1];
                z = e.Values[2];
                if (e.Values.Count == 3)
                {
                    // Fixes missing w.
                    w = QuaternionMath.CalculateW(ref x, ref y, ref z);
