                        QuaternionMath.Normalize(ref w, ref x, ref y, ref z);
                }
                attitude. SetFrom(x, y, z, w, nextNanoSec, dtSeconds);

}
            else
            {
#if DEBUG
                if (System.Diagnostics.Debugger.IsAttached)
                    System.Diagnostics.Debug.WriteLine($"\n Weird dt={dtNano * 1e-6}ms between {nanoSecond * 1e-9}s and {nextNanoSec * 1e-9}s.\n");
#endif
                attitude. DtSeconds = 0;
            }
            return attitude;
        }
