{
    private readonly List<int> _list = new();

    [Params(10, 100, 1_000, 100_000, 1_000_000)]
    public int Count { get; set; }

    [GlobalSetup]
    public void Setup()
    {
        for (int i = 0; i < Count; i++)
        {
            _list.Add(i);
        }
    }

    [GlobalCleanup]
    public void Cleanup()
    {
        _list.Clear();
    }

    [Benchmark]
    public int[] ToArray_Linq()
    {
        return _list.ToArray();
    }

    [Benchmark]
    public int[] ToArray_Literal()
    {
        return [.. _list];
    }
}
```
