    public static void Example()
    {
        var obj = new MyClass
        {
            //
        };
    }
}
""";
        var position = code.IndexOf("//");

        using var workspace = new AdhocWorkspace();
        var project = workspace.AddProject("TestProject", LanguageNames.CSharp);

        var document = workspace.AddDocument(project.Id, "Test.cs", SourceText.From(code));

        var service = CompletionService.GetService(document)!;
        var completionList = await service.GetCompletionsAsync(document, position);
        foreach (var item in completionList.ItemsList)
        {
            Console.WriteLine(item.DisplayText); // This should include "ExtensionProperty"
        }
    }
}
